	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (lower(title) like lower('%${search}%')) or (lower(writer) like lower('%${search}%')) or (lower(nonTagContent) like lower('%${search}%'))) where rown between #{start} and #{end} 


	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (lower(title) like lower('%${search}%'))) where rown between #{start} and #{end}


	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (lower(writer) like lower('%${search}%'))) where rown between #{start} and #{end}


	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (lower(nonTagContent) like lower('%${search}%'))) where rown between #{start} and #{end}
